import React, { Component } from 'react';

class AddGame extends Component {
    state = {
        img: '',
        title: '',
        description: '',
        price: ''
    }

    handleFormChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleGameSubmit = (e) => {
        e.preventDefault();
        this.props.addGame(this.state)

    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleGameSubmit}>
                    <input placeholder="Image" type="text" name="img" value={this.state.url} onChange={this.handleFormChange} />
                    <input placeholder="Game Name" type="text" name="title" value={this.state.gameName} onChange={this.handleFormChange} />
                    <input placeholder="Description" type="textarea" name="description" value={this.state.description} onChange={this.handleFormChange} />
                    <input placeholder="Price" type="text" name="price" value={this.state.price} onChange={this.handleFormChange} />
                    <button>Submit</button>
                </form>
            </div>

        )
    }
}

export default AddGame;
