import React, { Component } from 'react';

class AddGame extends Component {
    state = {
        img: '',
        gameName: '',
        description: '',
        price: ''
    }

    handleFormChange = (e) => {
        console.log(e.target.name)
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        const { addGame, handleGameSubmit } = this.props
        return (
            <div>
                <form onSubmit={handleGameSubmit}>
                    <input placeholder="URL" type="text" name="url" value={this.state.url} onChange={this.handleFormChange} />
                    <input placeholder="Game Name" type="text" name="gameName" value={this.state.gameName}  onChange={this.handleFormChange} />
                    <input placeholder="Description" type="textarea" name="description" value={this.state.description}  onChange={this.handleFormChange} />
                    <input placeholder="Price" type="text" name="price" value={this.state.price} onChange={this.handleFormChange} />
                    <button type='submit'>Submit</button>
                </form>
            </div>

        )
    }
}

export default AddGame;
