import React, { Component } from 'react';
import './App.css';
import GamesContainer from './components/Frontend/GamesContainer/GamesContainer';
import SearchMenu from './components/Frontend/SearchMenu/SearchMenu';

import CallOfDuty from './components/Games/CallOfDuty.jpeg';
import CounterStrike from './components/Games/CounterStrike.jpeg';
import Fifa from './components/Games/Fifa.jpeg';
import marioKart from './components/Games/marioKart.jpeg';
import MetalGearSolid from './components/Games/MetalGearSolid.jpeg';
import Sniper from './components/Games/Sniper.jpeg';


const games = [{
 title: "Call of Duty",
 price: 123,
 details: "qwewr",
 img: CallOfDuty
},
{
  title: "Counter-Strike",
  price: 123,
  details: "A first person shooter game depicting death matches between terrorists and counter-terrorists.",
  img: CounterStrike
 },
 {
  title: "Fifa",
  price: 123,
  details: "FIFA 19 brings you a conclusion to The Journey and all new game modes and features to enjoy on and offline. Fifa 19 allows you to take control in your very own footballing world.",
  img: Fifa
 },
 {
  title: "Mario Kart",
  price: 123,
  details: "Mario and the rest race against each other for the gold cup again. Underwater and air gliding returns. Mario Kart 8 introduces zero gravity, and has a total of 48 race tracks.",
  img: marioKart
 },
 {
  title: "Metal Gear Solid",
  price: 123,
  details: "qwewr",
  img: MetalGearSolid
 },
 {
  title: "Sniper",
  price: 123,
  details: "qwewr",
  img: Sniper
 }]

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      search: ''
    }
  }

  handleChange = (searchTerm) => {
     this.setState({
       search: searchTerm
     })   
  }

  filterSearch = () => {
    return games.filter(game => game.title.includes(this.state.search))
  }



  render() {
    return (
      <div className="games">
        < SearchMenu search={this.handleChange} />
        < GamesContainer games={this.filterSearch()} />
      </div>
    )
  }

}

export default App;
